import React from "react";

const [favorites, setFavorites] = useState([]);

// Function to remove a city from favorites
const handleRemoveFavorite = (cityToRemove) => {
  setFavorites(favorites.filter((city) => city !== cityToRemove));
};

// Render Favorites component
<Favorites


  onRemoveFavorite={handleRemoveFavorite}
/>;

const Favorites = ({ favorites, onSelectFavorite, onRemoveFavorite }) => {
  return (
    <div className="favorites">
      <h3>Favorite Locations</h3>
      <ul>
        {favorites.map((city, index) => (
          <li
            key={index}
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <span
              onClick={() => onSelectFavorite(city)}
              style={{ cursor: "pointer" }}
            >
              {city}
            </span>
            <button
              onClick={() => onRemoveFavorite(city)}
              style={{
                background: "none",
                border: "none",
                color: "red",
                cursor: "pointer",
              }}
            >
              &minus;
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Favorites;
