// src/components/WeatherDashboard.js

import React, { useState } from "react";
import SearchBox from "./SearchBox";
import WeatherCard from "./WeatherCard";
import Favorites from "./Favorites";

const WeatherDashboard = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [favorites, setFavorites] = useState([]);

  const handleSearch = (city) => {
    // Fetch weather data from API based on the city
    fetch(
      `https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=YOUR_API_KEY`
    )
      .then((response) => response.json())
      .then((data) => setWeatherData(data))
      .catch((error) => console.error("Error fetching weather data:", error));
  };

  const handleAddFavorite = (city) => {
    if (!favorites.includes(city)) {
      setFavorites([...favorites, city]);
    }
  };

  const handleRemoveFavorite = (city) => {
    setFavorites(favorites.filter((favorite) => favorite !== city));
  };

  const handleSelectFavorite = (city) => {
    // Fetch weather data for the selected favorite city
    handleSearch(city);
  };

  return (
    <div className="weather-dashboard">
      <SearchBox onSearch={handleSearch} />
      {weatherData && (
        <WeatherCard data={weatherData} onAddFavorite={handleAddFavorite} />
      )}
      <Favorites
        favorites={favorites}
        onSelectFavorite={handleSelectFavorite}
        onRemoveFavorite={handleRemoveFavorite}
      />
    </div>
  );
};

export default WeatherDashboard;
