  };

  const handleSelectFavorite = (city) => {
    // Logic to fetch weather data for selected favorite city...
  };

  const handleRemoveFavorite = (cityToRemove) => {
    setFavorites(favorites.filter((city) => city !== cityToRemove));
  };

  return (
    <div className="weather-dashboard">
      <SearchBox onSearch={handleSearch} />
      {weatherData && <WeatherCard data={weatherData} />}
      <Favorites
        favorites={favorites}
        onSelectFavorite={handleSelectFavorite}
        onRemoveFavorite={handleRemoveFavorite} // Passing function to child
      />
    </div>
  );
};

export default WeatherDashboard;
