//     }
//   };

     const handleKeyDown = (event) => {
       if (event.key === "Enter") {
         handleSearch();
       }
     };

  return (
   <div className="search-box">
      <input
        type="text"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        placeholder="Enter Location"
        onKeyDown={handleKeyDown}  
      />
    </div>
  );
};

export default SearchBox;
