import React, { useState } from "react";
import SearchBox from "./components/SearchBox";
import WeatherCard from "./components/WeatherCard";
import Favorites from "./components/Favorites";
import CurrentLocationDetails from "./CurrentLocationDetails";;

const App = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [favorites, setFavorites] = useState([]);
const [currentWeather, setCurrentWeather] = useState(null);
  const apiKey = "5ba1ba361346bd0040e36995c3799606";

  const fetchWeatherData = (city) => {
    const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=${apiKey}`;

    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        if (data.cod === 200) {
          setWeatherData(data);
        } else {
          alert("City not found");
        }
      })
      .catch((error) => {
        console.error("Error fetching weather data:", error);
      });
  };

  const addFavorite = (city) => {
    if (!favorites.includes(city)) {
      setFavorites([...favorites, city]);
    }
  };

  const selectFavorite = (city) => {
    fetchWeatherData(city);
  };

      const handleRemoveFavorite = (city) => {
        setFavorites(favorites.filter((favorite) => favorite !== city));
      };

  return (
    <div className="container">
      <h1>Weather Dashboard</h1>
      <SearchBox onSearch={fetchWeatherData} />
      <WeatherCard weatherData={weatherData} onAddFavorite={addFavorite} />
      <Favorites
        favorites={favorites}
        onSelectFavorite={selectFavorite}
              onRemoveFavorite={handleRemoveFavorite}
          />

    </div>
  );
};

// Function to remove a city from favorites

// Render Favorites component

export default App;
